  function refreshTime(){
    setTime(new Date().toLocaleTimeString());
  }
  return (
    <div className="container">
      <h1>{setcurrentTime.slice(0,7)}</h1>
      <button onClick={refreshTime}>Get Time</button>
    </div>
  );
}

export default App;
